import { IInitiativeModel } from "@esri/hub-common";
/**
 * Properties required to generate an Initiative Model from a Template
 *
 * @export
 * @interface IInitiativeTemplateOptions
 */
export interface IInitiativeTemplateOptions {
    title: string;
    initiativeKey: string;
    groupIds?: IInitiativeGroupIds;
}
/**
 * Hash for passing groupIds corresponding to newly created initiative
 * groups into the new initiative model
 *
 * @export
 * @interface IInitiativeGroupIds
 */
export interface IInitiativeGroupIds {
    [s: string]: string;
}
/**
 * Given an Initiative Template model, create a new Initiative model
 * Note: this does not save the model. It just sets up the new model.
 *
 * @export
 * @param {IInitiativeModel} template
 * @param {*} options
 * @param {IRequestOptions} requestOptions
 * @returns {Promise<IInitiativeModel>}
 */
export declare function createInitiativeModelFromTemplate(template: IInitiativeModel, options: IInitiativeTemplateOptions): IInitiativeModel;
